import mutant
import mstringfix
import covid

SEQUENCE=""

CLADE_MUTATIONS=dict()

def wuhan_wildtype(site):
    if site<1 or site>len(SEQUENCE):
        return None
    return SEQUENCE[site-1]

def clade_defined(name):
    return bool(name in covid.BASELINE_MSTRINGS)

def clade_init(name):
    global CLADE_MUTATIONS
    if clade_defined(name):
        CLADE_MUTATIONS[name]=dict()
        mstring = covid.BASELINE_MSTRINGS[name]
        mstring = mstringfix.mstring_brackets(mstring)
        ssmlist = mutant.Mutation.from_mstring(mstring)
        for ssm in ssmlist:
            CLADE_MUTATIONS[name][ssm.site]=ssm
        
def clade_wildtype(name,site):
    if not clade_defined(name):
        return wuhan_wildtype(site)
    if name not in CLADE_MUTATIONS:
        clade_init(name)
    ssm = CLADE_MUTATIONS[name].get(site,None)
    return ssm.mut if ssm else wuhan_wildtype(site)



